import ExpenseItem from "./ExpenseItem";
import Card from '../UI/Card';
import './Expenses.css';
import ExpensesFilter from "./ExpensesFilter";
import { useState } from "react";


const Expenses = (props) => {

    const [selectedExpenseFilter, setSelectedExpenseFilter] = useState('');
    const expensesFilterHandler = (filterData) => {
        setSelectedExpenseFilter(filterData);
    }

    const filteredExpenses = props.expenseList.filter(expense => {
        return expense.date.getFullYear.toString
    });

    return (
        <div>
            <Card className='expenses'>
                <ExpensesFilter selected={selectedExpenseFilter} onExpensesFilterSelect={expensesFilterHandler} />
                {props.expenseList.map((expense) => (
                    <ExpenseItem
                        key={expense.id}
                        title={expense.title}
                        amount={expense.amount}
                        date={expense.date}
                    />
                ))}
            </Card>
        </div>
    );
};

export default Expenses;
