import ExpenseItem from "./ExpenseItem";
import Card from '../UI/Card';
import './Expenses.css';
import ExpensesFilter from "./ExpensesFilter";
import { useState } from "react";


const Expenses = (props) => {

    const [selectedExpenseFilter, setSelectedExpenseFilter] = useState('');
    const expensesFilterHandler = (filterData) => {
        console.log(filterData);
        setSelectedExpenseFilter(filterData);
    }

    return (
        <div>
            <Card className='expenses'>
                <ExpensesFilter onExpensesFilterSelect={expensesFilterHandler}/>
                {props.expenseList.map((expense, index) => (
                    <ExpenseItem 
                        key={expense.id}
                        title={expense.title}
                        amount={expense.amount}
                        date={expense.date} 
                    />
                ))}
            </Card>
        </div>
    );
};

export default Expenses;
